css:
table { border-spacing: 0; border-collapse: collapse; } 
th, td { line-height: 1.2; font-size: 13px; font-weight: bold; padding: 3px; margin: 0; } 
.ar { text-align: right; } 
.styled-table { border-collapse: collapse; box-shadow: 0 0 20px rgba(0, 0, 0, 0.10); border-radius: 10px; overflow: hidden; border: 1px solid #dddddd; } 
.styled-table tbody tr:last-of-type { border-bottom: 2px solid {{color}}; } 
.styled-table th, .styled-table td { border: none; font-weight: bold; } 
.styled-table tbody tr:nth-of-type(even) { background-color: #f3f3f3; } 
.items-table { border: none !important; } 
.items-table td, .items-table th { border: none !important; border-bottom: 1px solid #eee !important; } 
.items-table tbody tr:nth-of-type(even) { background-color: #f9f9f9; } 
.print-format { padding: 0; font-size: 13px; direction: ltr; } 
.cutting-service-section { background-color: #e8f5e9; border: 2px solid #4caf50; border-radius: 8px; padding: 8px 15px; margin: 5px 0; text-align: center; } 
.page-footer { position: fixed; bottom: 0; left: 0; right: 0; width: 100%; height: 25mm; text-align: center; border-top: 2px solid {{color}}; padding: 3px 8px; background: white; box-sizing: border-box; }}; padding: 5px 10px; background: white; z-index: 9999; } 
@page { size: A4; margin: 5mm 5mm 30mm 5mm; } 
@media print { .print-format-gutter { margin: 0 !important; } 
.print-format { margin: 0 !important; padding: 0 !important; }

html:
{% set customer = doc.customer %}
{% set ctax_id = frappe.get_doc("Customer", customer).tax_id %}
{% if doc.customer_address %}
    {% set addr = frappe.get_doc("Address", doc.customer_address) %}
{% endif %}
{% set comp = frappe.get_doc("Company", doc.company) %}
{% set company_logo = "/assets/expenses_management/images/logo.jpeg" %}
{% set color = "#333333" %}
{% set links = frappe.get_all('Dynamic Link', filters={'link_doctype': 'Company', 'link_name': doc.company, 'parenttype': 'Address'}, fields=['parent']) %}
{% if links %}
    {% set address = frappe.get_doc("Address", links[0].parent) %}
{% endif %}

<style>
@page {
    size: A4;
    margin: 8mm 8mm 8mm 8mm;
}

html, body {
    height: 100%;
    margin: 0;
    padding: 0;
}

.print-format {
    height: 100%;
    padding: 0;
    margin: 0;
}

/* Main wrapper table to push footer to bottom */
.page-wrapper {
    width: 100%;
    min-height: 275mm;
    border-collapse: collapse;
}

.page-wrapper > tbody > tr > td {
    padding: 0;
    border: none;
}

.content-cell {
    vertical-align: top;
}

.footer-cell {
    vertical-align: bottom;
    height: 1px;
}

.styled-table {
    border-collapse: collapse;
    box-shadow: 0 0 20px rgba(0, 0, 0, 0.15);
    border-radius: 10px;
    overflow: hidden;
    border: 1px solid #dddddd;
}

.styled-table td {
    border: none;
    padding: 4px 8px;
}

.styled-table tr:nth-of-type(even) {
    background-color: #f3f3f3;
}

.items-table {
    border-collapse: collapse;
}

.items-table td, .items-table th {
    border: none;
    border-bottom: 1px solid #ddd;
    padding: 4px;
}

.items-table tr:nth-of-type(even) {
    background-color: #f9f9f9;
}

.cutting-service-section {
    background-color: #e8f5e9;
    border: 2px solid #4caf50;
    border-radius: 8px;
    padding: 8px;
    margin: 10px 0;
    text-align: center;
}

.footer-content {
    border-top: 2px solid #000;
    padding: 8px;
    display: flex;
    justify-content: space-between;
    align-items: center;
    background: white;
}

@media print {
    html, body, .print-format {
        height: 100%;
    }
    .page-wrapper {
        min-height: 275mm;
    }
}
</style>

<table class="page-wrapper">
<tbody>
<tr>
<td class="content-cell">

<header style="display:flex; justify-content: space-between; align-items: center">

     <div style="text-align: center">
        {% if doc.is_return == 0 %}
                <div style="font-size:28px;font-weight:bold;color:#1a5276;">فاتورة ضريبية</div>
                <div style="font-size:24px;font-weight:bold;color:#1a5276;">TAX INVOICE</div>
        {% else %}
                <div style="font-size:28px;font-weight:bold;color:#1a5276;">مرتجع</div>
                <div style="font-size:24px;font-weight:bold;color:#1a5276;">Credit Note</div>
        {% endif %}

        {% if doc.custom_credit == 1 %}
                <div style="color:#e67e22;font-size:12px;font-weight:bold;margin-top:2px;padding:1px 8px;background-color:#fef5e7;border-radius:3px;display:inline-block;border:1px solid #e67e22;">آجل / Credit</div>
        {% else %}
                <div style="color:#27ae60;font-size:12px;font-weight:bold;margin-top:2px;padding:1px 8px;background-color:#eafaf1;border-radius:3px;display:inline-block;border:1px solid #27ae60;">مدفوع / Paid</div>
        {% endif %}

        {% if doc.docstatus == 2 %}
                <div style="color:#c0392b;font-size:10px;font-weight:bold;margin-top:1px;padding:1px 6px;background-color:#fadbd8;border-radius:2px;display:inline-block;">CANCELLED / ملغية</div>
        {% elif doc.docstatus == 0 %}
                <div style="color:#d35400;font-size:10px;font-weight:bold;margin-top:1px;padding:1px 6px;background-color:#fdebd0;border-radius:2px;display:inline-block;">DRAFT / مسودة</div>
        {% endif %}
     </div>
         <img 
        style="height:160px;width:auto;max-width:360px;" 
        alt="Logo"
        src="{{ company_logo }}" 
     />
</header>


<!-- Date Row -->
<table class="styled-table" style="width:100%;margin:2px 0;">
    <tr>
        <td style="font-size:12px;text-align:center;">{{ doc.due_date or '' }}</td>
        <td style="font-size:12px;text-align:center;">Due Date / الاستحقاق</td>
        <td style="font-size:12px;text-align:center;">{{ doc.posting_date }}</td>
        <td style="font-size:12px;text-align:center;">Issue Date / الاصدار</td>
        <td style="font-size:12px;text-align:center;">{{ doc.name }}</td>
        <td style="font-size:12px;text-align:center;">Invoice No / رقم الفاتورة</td>
    </tr>
</table>

<!-- Customer and Company Info -->
<table style="width:100%;border:none;margin:2px 0;">
    <tr>
        <td style="width:49%;border:none;vertical-align:top;padding-right:5px;">
            <table class="styled-table" style="width:100%;">
                {% if doc.customer %}
                    <tr>
                        <td style="font-size:12px;">To / الى</td>
                        <td style="font-size:12px;text-align:center;">{{ doc.customer }}</td>
                    </tr>
                {% endif %}

                {% if doc.customer_name %}
                    <tr>
                        <td style="font-size:12px;">Customer / العميل</td>
                        <td style="font-size:12px;text-align:center;">{{ doc.customer_name }}</td>
                    </tr>
                {% endif %}

                {% if ctax_id %}
                    <tr>
                        <td style="font-size:12px;">TAX ID / الرقم الضريبي</td>
                        <td style="font-size:12px;text-align:center;">{{ ctax_id }}</td>
                    </tr>
                {% endif %}

                {% if addr and addr.address_line1 %}
                    <tr>
                        <td style="font-size:12px;">Address / العنوان</td>
                        <td style="font-size:12px;text-align:center;">{{ addr.address_line1[:30] }}</td>
                    </tr>
                {% endif %}

                {% if addr and addr.phone %}
                    <tr>
                        <td style="font-size:12px;">Phone / الهاتف</td>
                        <td style="font-size:12px;text-align:center;">{{ addr.phone }}</td>
                    </tr>
                {% endif %}
            </table>
        </td>

        <td style="width:49%;border:none;vertical-align:top;padding-left:5px;">
            <table class="styled-table" style="width:100%;">
                {% if doc.company %}
                    <tr>
                        <td style="font-size:12px;">From / من</td>
                        <td style="font-size:12px;text-align:center;">{{ doc.company }}</td>
                    </tr>
                {% endif %}

                {% if doc.company_tax_id %}
                    <tr>
                        <td style="font-size:12px;">TAX ID / الرقم الضريبي</td>
                        <td style="font-size:12px;text-align:center;">{{ doc.company_tax_id }}</td>
                    </tr>
                {% endif %}

                {% if comp.cr_number %}
                    <tr>
                        <td style="font-size:12px;">CR Number / السجل التجاري</td>
                        <td style="font-size:12px;text-align:center;">{{ comp.cr_number }}</td>
                    </tr>
                {% endif %}

                {% if address and address.address_line1 %}
                    <tr>
                        <td style="font-size:12px;">Address / العنوان</td>
                        <td style="font-size:12px;text-align:center;">{{ address.address_line1[:30] }}</td>
                    </tr>
                {% endif %}

                {% if comp.phone_no %}
                    <tr>
                        <td style="font-size:12px;">Phone / الهاتف</td>
                        <td style="font-size:12px;text-align:center;">{{ comp.phone_no }}</td>
                    </tr>
                {% endif %}
            </table>
        </td>
    </tr>
</table>

<!-- Items Table - NO BORDER -->
<table class="items-table" style="width:100%;margin-top:2px;border:none;">
    <tr style="background-color:{{color}};border:none;">
        <td colspan="5" style="text-align:left;color:white;font-size:15px;border-top-left-radius:10px;border:none;">Item and Services Description</td>
        <td colspan="4" style="text-align:right;color:white;font-size:15px;border-top-right-radius:10px;border:none;">توصيف السلعة أو الخدمة</td>
    </tr>

    <tr style="font-weight:bold;text-align:center;background-color:#f0f0f0;">
        <td style="width:3%;font-size:11px;border:none;">S.N<br>ر.ت</td>
        <td style="font-size:11px;border:none;">Nature of goods / تفاصيل الصنف</td>
        <td style="width:7%;font-size:11px;border:none;">Qty<br>الكمية</td>
        <td style="width:10%;font-size:11px;border:none;">Unit price<br>سعر الوحدة</td>
        <td style="width:5%;font-size:11px;border:none;">Unit<br>الوحدة</td>
        <td style="width:13%;font-size:11px;border:none;">Taxable Amt<br>المبلغ الخاضع</td>
        <td style="width:8%;font-size:11px;border:none;">Tax %<br>الضريبة</td>
        <td style="width:10%;font-size:11px;border:none;">Tax Amt<br>مبلغ الضريبة</td>
        <td style="width:14%;font-size:11px;border:none;">Subtotal<br>المجموع</td>
    </tr>

    {% set i = [1] %}
    {% for item in doc.items %}
        {% set item_tax_rate = 0 %}{% set item_tax_amount = 0 %}
        {% if doc.taxes and doc.taxes|length > 0 %}
            {% set item_tax_rate = doc.taxes[0].rate or 15 %}
            {% set item_tax_amount = (item.net_amount or 0) * item_tax_rate / 100 %}
        {% endif %}
        {% set item_total = (item.net_amount or 0) + item_tax_amount %}

        <tr>
            <td style="font-size:13px;text-align:center;border:none;">{{ i[0] }}</td>
            <td style="font-size:13px;border:none;">{{ item.item_code }}: {{ item.item_name[:70] }}</td>
            <td style="font-size:13px;text-align:center;border:none;">
                {% if (item.qty | int) == item.qty %}
                    {{ item.qty | int }}
                {% else %}
                    {{ item.qty }}
                {% endif %}
            </td>
            <td style="font-size:13px;text-align:center;border:none;">{{ "{:,.2f}".format(item.rate) }}</td>
            <td style="font-size:13px;text-align:center;border:none;">{{ item.uom }}</td>
            <td style="font-size:13px;text-align:center;border:none;">{{ "{:,.2f}".format(item.net_amount or item.amount) }}</td>
            <td style="font-size:13px;text-align:center;border:none;">{{ item_tax_rate }}%</td>
            <td style="font-size:13px;text-align:center;border:none;">{{ "{:,.2f}".format(item_tax_amount) }}</td>
            <td style="font-size:13px;text-align:center;border:none;">{{ "{:,.2f}".format(item_total) }}</td>
        </tr>

        {% if i.append(i.pop() + 1) %}{% endif %}
    {% endfor %}
</table>

<!-- Cutting Service Section -->
<div class="cutting-service-section">
    <span style="font-size:16px;font-weight:bold;color:#2e7d32;">خدمة القص مجانية من الشركة / Free Cutting Service from the Company</span>
</div>

<!-- Totals Section -->
<table style="width:100%;border:none;margin-top:2px;">
    <tr style="background-color:{{color}};">
        <td colspan="2" style="color:white;font-size:15px;border-radius:10px 10px 0 0;text-align:center;border:none;">Total amounts / اجمالي المبالغ</td>
    </tr>

    <tr style="border:none;">
        <td style="width:50%;vertical-align:top;border:none;padding:5px;">
            <table style="width:100%;border:none;">
                <tr style="background-color:#ffffff;">
                    <td style="font-size:12px;border:none;">Total (Excluding VAT) / الإجمالي غير شامل</td>
                    <td style="font-size:12px;text-align:right;border:none;">SAR {{ "{:,.2f}".format(doc.net_total) }}</td>
                </tr>

                {% if doc.discount_amount %}
                    <tr style="background-color:#f3f3f3;">
                        <td style="font-size:12px;border:none;">Discount / الخصومات</td>
                        <td style="font-size:12px;text-align:right;border:none;">SAR {{ "{:,.2f}".format(doc.discount_amount) }}</td>
                    </tr>
                {% endif %}

                <tr style="background-color:#f3f3f3;">
                    <td style="font-size:12px;border:none;">Total VAT / ضريبة القيمة المضافة</td>
                    <td style="font-size:12px;text-align:right;border:none;">SAR {{ "{:,.2f}".format(doc.total_taxes_and_charges) }}</td>
                </tr>

                <tr style="background-color:#ffffff;">
                    <td style="font-size:14px;font-weight:bold;border:none;">Total Amount Due / إجمالي المستحق</td>
                    <td style="font-size:14px;font-weight:bold;text-align:right;border:none;">SAR {{ "{:,.2f}".format(doc.grand_total) }}</td>
                </tr>

                <tr style="background-color:#f3f3f3;">
                    <td colspan="2" style="font-size:12px;border:none;">{{ frappe.utils.money_in_words(doc.grand_total | abs, 'SAR', 'Halala') }}</td>
                </tr>

                <tr style="background-color:#ffffff;">
                    <td style="font-size:12px;border:none;">Issued By / تحرر بواسطة:</td>
                    <td style="font-size:12px;text-align:right;border:none;">{{ frappe.db.get_value('User', doc.modified_by, 'full_name') }}</td>
                </tr>
            </table>
        </td>

        <td style="width:50%;vertical-align:middle;border:none;text-align:center;padding:10px;">
            {% if doc.ksa_einv_qr %}
                <img src="{{ doc.ksa_einv_qr }}" style="width:140px;height:140px;" />
            {% endif %}
        </td>
    </tr>
</table>

</td>
</tr>

<!-- Footer Row - At Bottom of Last Page Only -->
<tr>
<td class="footer-cell">
<div class="footer-content">
    <div>
        <div style="font-size:13px;font-weight:bold;color:#333;">
        الرياض: الصناعية القديمة: 0500174187 | طريق الخرج: 0531674322
        </div>
        <div style="font-size:13px;font-weight:bold;color:#333;margin-top:3px;">
        طريق هارون الرشيد: 0558501303 | جدة: 0533317381
        </div>
    </div>
    <img src="{{ company_logo }}" style="height:100px;width:auto;" alt="Logo" />
</div>
</td>
</tr>

</tbody>
</table>
